import React from 'react';
import axios from 'axios';
import { useState } from 'react';


const TeamForm = ({ data }) => {

    const [equipo, setEquipo] = useState({
    nombre: "",
    categoria: "",
    deporte: "",
    puntaje: 0,
    responsable_1_id: "",
    responsable_2_id: ""
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEquipo(prevState => ({
            ...prevState,
            [name]: value
        }));
    };


    const baseUrl = "http://127.0.0.1:5000/subir_equipo";

    /*const handleSubmit = (event) => {
        event.preventDefault();
        if (post.name !== "" && post.email !== "") {
          axios.post(baseUrl, post);
          setPost({ name: "", email: "" });
        }
      };*/

    return (
        <form className="simple-form">
            <div className="form-group">
                <label htmlFor="nombre">Nombre del Equipo:</label>
                <input type="text" id="nombre" name="nombre" />
            </div>
            <div className="form-group">
                <label htmlFor="deporte">Deporte:</label>
                <input type="text" id="deporte" name="deporte"/>
            </div>
            <div className="form-group">
                <label htmlFor="categoria">Categor√≠a:</label>
                <input type="text" id="categoria" name="categoria"/>
            </div>
            <div className="form-group">
                <label htmlFor="puntaje">Puntaje:</label>
                <input type="number" id="puntaje" name="puntaje"/>
            </div>
            <div className="form-group">
                <label htmlFor="responsable_1">Responsable 1:</label>
                <input type="text" id="responsable_1" name="responsable_1"/>
            </div>
            <div className="form-group">
                <label htmlFor="responsable_2">Responsable 2:</label>
                <input type="text" id="responsable_2" name="responsable_2"/>
            </div>
            <button type="submit">Enviar</button>
        </form>
  );
}


export default TeamForm;
